import axios from 'axios';
import React, { useCallback, useEffect } from 'react';
import '../css/Buttons.css';

const Buttons = ({ onListGet, lists }) => {

    const clickDeleteLast = () => {
        onListGet();
    }

    const deleteLast = useCallback (async() => {
        const id = lists[lists?.length - 1]?._id;

        if(id!==undefined){
            await axios.delete(`/api/lists/delete/${id}`)
                .then((res) => {
                    console.log(res);
                    alert('마지막 항목이 삭제되었습니다.');
                })
                .catch((err) => {
                    console.log(err);
                })
        }

    },[lists])

    useEffect(()=>{
        deleteLast();
    }, [deleteLast])

    const deleteAll = async() => {
        await axios.delete('/api/lists/delete')
        .then((res)=>{
            console.log(res);
            alert('모든 항목이 삭제되었습니다.');
        })
        .catch((err)=>{
            console.log(err);
        })
    }

    return (
        <div className="btn-area">
            <button type="button" id="btnDelLast" onClick={() => clickDeleteLast()}>마지막 항목 삭제</button>
            <button type="button" id="btnDelAll" onClick={() => deleteAll()}>전체 삭제</button>
        </div>
    )
}

export default Buttons
